script:
function envVar(varName) {
  return $.NSProcessInfo
    .processInfo
    .environment
    .objectForKey(varName).js
}

function setWallpaper(path) {
  Application("System Events").currentDesktop.picture = path
}

function run(argv) {
  const targetPath = envVar("target_path")

  if (targetPath) return setWallpaper(targetPath)
  else if (argv[0]) return setWallpaper(argv[0])
  else throw "Either a valid input or configuration is required"
}
