script:
function envVar(varName) {
  return $.NSProcessInfo
    .processInfo
    .environment
    .objectForKey(varName).js
}

function setWallpaper(path) {
  Application("System Events").currentDesktop.picture = path
}

function run(argv) {
  const targets = envVar("target_path") ? envVar("target_path").split("\n") : argv

  if (targets.length === 0) throw "Either a valid input or configuration is required"

  return setWallpaper(targets[0]) 
}
