script:
function envVar(varName) {
  return $.NSProcessInfo
    .processInfo
    .environment
    .objectForKey(varName).js
}

function setWallpaper(path) {
  Application("System Events").currentDesktop.picture = path
}

function run(argv) {
  const single_input = envVar("target_path") ? [envVar("target_path")] : argv

  if (single_input.length > 1) throw "Expecting single input"
  if (single_input.length === 0) throw "Expecting valid input or configuration"

  return setWallpaper(single_input[0]) 
}
