function alfredArgs(args) {
  return JSON.stringify({ alfredworkflow: { arg: args } })
}

const systemEvents = Application("System Events")
const wallpaperDir = systemEvents.currentDesktop.picturesFolder()
const wallpaperPaths = systemEvents.folders.byName(wallpaperDir).diskItems().map(p => p.toString())
const wallpaperRandom = wallpaperPaths[Math.floor(Math.random() * wallpaperPaths.length)]

systemEvents.currentDesktop.picture = wallpaperRandom
alfredArgs(wallpaperRandom)
