script:
ObjC.import("stdlib")

function alfredArgs(args) {
  return JSON.stringify({ alfredworkflow: { arg: args } })
}

const match_string = $.getenv("match_string")
const regex = $.getenv("regex") === "1" ? true : false

function run(argv) {
  return regex ?
    alfredArgs(argv.filter(a => !a.match(match_string))) :
    alfredArgs(argv.filter(a => !a.includes(match_string)))
}
