script:
function alfredArgs(args) {
  return JSON.stringify({ alfredworkflow: { arg: args } })
}

function envVar(varName) {
  return $.NSProcessInfo
    .processInfo
    .environment
    .objectForKey(varName).js
}

function run(argv) {
  const targets = envVar("target_path") ? envVar("target_path").split("\n") : argv
  const fileManager = $.NSFileManager.defaultManager

  if (targets.length === 0) throw "Expecting valid input or configuration"

  const paths = targets.map(path => $(path).stringByExpandingTildeInPath.js)

  paths.forEach(path => { if (!path.startsWith("/")) throw "Invalid path: " + path })

  paths.forEach(path => {
    fileManager.createDirectoryAtPathWithIntermediateDirectoriesAttributesError(path, true, undefined, undefined)
  })

  return alfredArgs(paths)
}
