function alfredArgs(args) {
  return JSON.stringify({ alfredworkflow: { arg: args } })
}

function run(argv) {
  const fileManager = $.NSFileManager.defaultManager
  const paths = argv.map(path => $(path).stringByExpandingTildeInPath.js)

  paths.forEach(path => { if (!path.startsWith("/")) throw "Invalid path: " + path })

  paths.forEach(path => {
    fileManager.createDirectoryAtPathWithIntermediateDirectoriesAttributesError(path, true, undefined, undefined)
  })

  return alfredArgs(paths)
}
