script:
case "${out_format}" in
  sha256) shasum --algorithm 256 "${1}" | cut -d ' ' -f1 | tr -d '\n';;
  sha512) shasum --algorithm 512 "${1}" | cut -d ' ' -f1 | tr -d '\n';;
  sha1) shasum --algorithm 1 "${1}" | cut -d ' ' -f1 | tr -d '\n';;
  md5) md5 -q "${1}" | tr -d '\n';;
  *) echo "Invalid algorithm: ${out_format}" >&2; exit 1;;
esac
