script:
for file in "${@}"; do
  # Needs to be run on a file, not a directory
  if [[ ! -f "${file}" ]]; then
    echo "Invalid file: ${file}" >&2
    exit 1
  fi

  case "${target_mode}" in
    on) /bin/chmod +x "${file}";;
    off) /bin/chmod -x "${file}";;
    toggle) [[ -x "${file}" ]] && /bin/chmod -x "${file}" || /bin/chmod +x "${file}";;
    *) echo "Invalid mode: ${target_mode}" >&2; exit 1;;
  esac
done
