function alfredArgs(args) {
  return JSON.stringify({ alfredworkflow: { arg: args } })
}

function run(argv) {
  const paths = argv.map(path => $(path).stringByExpandingTildeInPath.js)
  const fileManager = $.NSFileManager.defaultManager

  paths.forEach(path => {
    if (!path.startsWith("/")) throw "Invalid path: " + path
    if (fileManager.fileExistsAtPath(path)) throw "File exists: " + path
  })

  paths.forEach(path => {
    fileManager.createDirectoryAtPathWithIntermediateDirectoriesAttributesError($(path).stringByDeletingLastPathComponent.js, true, undefined, undefined)
    fileManager.createFileAtPathContentsAttributes(path, undefined, undefined)
  })

  return alfredArgs(paths)
}
