script:
function alfredArgs(args) {
  return JSON.stringify({ alfredworkflow: { arg: args } })
}

function envVar(varName) {
  return $.NSProcessInfo
    .processInfo
    .environment
    .objectForKey(varName).js
}

function run(argv) {
  const noExtension = envVar("no_extension") === "1"

  if (noExtension) return alfredArgs(argv.map(path => $(path).stringByDeletingPathExtension.lastPathComponent.js))

  return alfredArgs(argv.map(path => $(path).lastPathComponent.js))
}
