script:
function alfredArgs(args) {
  return JSON.stringify({ alfredworkflow: { arg: args } })
}

function finderItemToPath(item) {
  return $.NSURL.URLWithString(item.url()).path.js
}

function run(argv) {
  const finder = Application("Finder")

  const duplicated = argv.map(p => finderItemToPath(finder.duplicate(Path(p))))
  return alfredArgs(duplicated)
}
