script:
function alfredArgs(args) {
  return JSON.stringify({ alfredworkflow: { arg: args } })
}

function finderItemToPOSIXPath(finderItem) {
  return $.NSURL.URLWithString(finderItem.url()).path.js
}

function run(argv) {
  const finder = Application("Finder")

  const duplicated = argv.map(p => finderItemToPOSIXPath(finder.duplicate(Path(p))))
  return alfredArgs(duplicated)
}
