script:
function alfred_args {
  /usr/bin/osascript -l JavaScript -e 'function run(argv) { return JSON.stringify({ alfredworkflow: { arg: argv } }) }' "${@}"
}

readonly image="${1}"
readonly width="$(../.common/get-image-property 'pixelWidth' "${image}")"
readonly height="$(../.common/get-image-property 'pixelHeight' "${image}")"

case "${property}" in
  width) echo -n "${width}";;
  height) echo -n "${height}";;
  both) alfred_args "${width}" "${height}";;
  *) echo "Invalid dimensions: ${property}" >&2; exit 1;;
esac
