script:
readonly image="${1}"
readonly width="$(../get-image-property 'pixelWidth' "${image}")"
readonly height="$(../get-image-property 'pixelHeight' "${image}")"

case "${property}" in
  width) echo -n "${width}";;
  height) echo -n "${height}";;
  both) echo -n "${width}x${height}";;
  *) echo "Invalid dimensions: ${property}" >&2; exit 1;;
esac
