script:
function alfredArgs(args) {
  return JSON.stringify({ alfredworkflow: { arg: args } })
}

function envVar(varName) {
  return $.NSProcessInfo
    .processInfo
    .environment
    .objectForKey(varName).js
}

const frontmost = Application("System Events").applicationProcesses.where({ frontmost: true })[0]

switch (envVar("out_format")) {
  case "name": alfredArgs(frontmost.name()); break
  case "bundle_id": alfredArgs(frontmost.bundleIdentifier()); break
  case "path": alfredArgs($.NSURL.URLWithString(frontmost.applicationFile().url()).path.js); break
  default: throw "Unrecognised type: " + envVar("out_format")
}
