ObjC.import("stdlib")

function run(argv) {
  const fromVar = $.getenv("app_list")
    .split(",")
    .map(app => app.trim())
    .filter(app => app.length > 0)

  const apps = argv.concat(fromVar)

  $.getenv("in_background") === "0" ?
    apps.forEach(app => Application(app).activate()) :
    apps.forEach(app => Application(app).launch())
}
