function envVar(varName) {
  return $.NSProcessInfo
    .processInfo
    .environment
    .objectForKey(varName).js
}

function openApp(app, toForeground) {
  // "activate" does not restore minimised windows, but "launch" does
  app.launch() // Open app in background
  if (toForeground) app.activate() // Bring to front if applicable
}

function run(argv) {
  const fromVar = envVar("app_list")
    .split(",")
    .map(app => app.trim())
    .filter(app => app.length > 0)

  const toForeground = envVar("in_background") === "0"
  const apps = argv.concat(fromVar)

  apps.forEach(appName => {
    try {
      const app = Application(appName)
      app.quit()
      while (app.running()) delay(0.2) // Asking to quit does not block execution, so wait until it exits
      openApp(app, toForeground)
    } catch (ignore) { }
  })
}
