script:
function envVar(varName) {
  return $.NSProcessInfo
    .processInfo
    .environment
    .objectForKey(varName).js
}

function isRunning(apps) {
  try {
    return apps.every(app => Application(app).running())
  } catch(error) {
    if (error.errorNumber === -2700) return false
  }
}

function run(argv) {
  const targets = envVar("target_app") ? [envVar("target_app")] : argv

  if (targets.length === 0) throw "Expecting valid input or configuration"

  return isRunning(targets)
}
