script:
function envVar(varName) {
  return $.NSProcessInfo
    .processInfo
    .environment
    .objectForKey(varName).js
}

function isRunning(apps) {
  try {
    return apps.every(app => Application(app).running())
  } catch(error) {
    if (error.errorNumber === -2700) return false
  }
}

function run(argv) {
  const targetApp = envVar("target_app")

  if (targetApp) return isRunning([targetApp])
  else if (argv[0]) return isRunning(argv)
  else throw "Either a valid input or configuration is required"
}
