function writeSTDOUT(string) {
  const nsstring = $.NSString
    .alloc
    .initWithString(string)
    .dataUsingEncoding($.NSUTF8StringEncoding)

  $.NSFileHandle
    .fileHandleWithStandardOutput
    .writeData(nsstring)
}

function run(argv) {
  // Output as string, without newline
  const state = argv.every(shortcut => Application("Shortcuts Events").shortcuts.byName(shortcut).exists()).toString()
  writeSTDOUT(state)
}
