script:
ObjC.import("stdlib")

const delimiter = $.getenv("delimiter")
const column = parseInt($.getenv("column")) - 1 // Correct user input for zero-based array
const trimOutput = $.getenv("trim_output") === "1" ? true : false

if (column < 0) throw "Column number must be higher than zero"

function run(argv) {
  return argv[0]
    .split("\n")
    .map(line => {
      return trimOutput ?
        line.split(delimiter)[column]?.trim() :
        line.split(delimiter)[column]
    })
    .join("\n")
}
