script:
ObjC.import("stdlib")

function alfredArgs(args) {
  return JSON.stringify({ alfredworkflow: { arg: args } })
}

function run(argv) {
  const hostnames = argv.map(url => $.NSURL.URLWithString(url).host.js)

  if ($.getenv("no_www") === "1") return alfredArgs(hostnames.map(url => url.replace(/^www\d*\./, "")))
  return alfredArgs(hostnames)
}
