script:
function envVar(varName) {
  return $.NSProcessInfo
    .processInfo
    .environment
    .objectForKey(varName).js
}

function alfredArgs(args) {
  return JSON.stringify({ alfredworkflow: { arg: args } })
}

function writeFile(path, text) {
  $.NSString
    .alloc
    .initWithUTF8String(text)
    .writeToFileAtomicallyEncodingError(path, true, $.NSUTF8StringEncoding, undefined)
}

function run(argv) {
  const webFiles = argv.map(url => {
    const urlObject = $.NSURL.URLWithString(url)
    const urlHost = urlObject.host.js
    const urlPath = urlObject.path.js
      .replace(/\/$/, "") // Remove trailing "/"
      .replace(/[^\w]/g, "_") // Replace non-word characters with "_"

    const file = (envVar("hostname_only") === "1" || urlPath === "") ?
      `${envVar("save_in")}/${urlHost}.webloc` :
      `${envVar("save_in")}/${urlHost}${urlPath}.webloc`

    if (envVar("overwrite") === "0" && $.NSFileManager.defaultManager.fileExistsAtPath(file)) throw `File exists: ${file}`

    const weblocContents = `<?xml version="1.0" encoding="UTF-8"?><!DOCTYPE plist PUBLIC "-//Apple//DTD PLIST 1.0//EN" "http://www.apple.com/DTDs/PropertyList-1.0.dtd"><plist version="1.0"><dict><key>URL</key><string>${url}</string></dict></plist>`

    writeFile(file, weblocContents)
    return file
  })

  return alfredArgs(webFiles)
}
